    const [addname,setAddName] = useState("")
    const initia = {
        todos:[{
          id:0,
          name:'dsf',
          comm:'sdfsd'
        }

        ]
      };





  return (
    <CommentLayout>
        <h1>눌러서 댓글보기</h1>
        <CommentContainer>

            <InputStyle type="text" value={addname} onChange={(event) =>{
                setAddName(event.target.value)
            }}/>


            <button>댓글 남기기</button>
        </CommentContainer>
        <Comments/>
    </CommentLayout>
  )
}

export default Comment

const CommentLayout = styled.div`
    border-top:1px solid #eee;
    padding: 30px;
`
const CommentContainer = styled.div`
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: center;
    justify-content: center;
    flex-direction: row;
    gap: 12px;
    width: 100%;
    padding: 0px 12px;
    margin-bottom: 30px;
`
const InputStyle = styled.input`
    box-sizing: border-box;
    height: 46px;
    width: 100%;
    outline: none;
    border-radius: 8px;
    padding: 0px 12px;
    font-size: 14px;
    border: 1px solid rgb(238, 238, 238);
`
